import React, { Component } from 'react';

class Counter extends Component {
    state={
        count: 0, 
        // imageUrl: "https://picsum.photos/300",
    };
    styles={
        fontSize: 50,
        fontWeight: "bold",
    };
    render() { 
        // let classes = "badge mr-2 badge-";
        // classes += this.state.count === 0? "warning": "primary";
        // this is dynamically rendering 


        // you can refactor above to a method: shortcut: ctrl+shift+r  then you can call the method directly in html
        // let classes = this.getBadgeClasses();


        return (
        <React.Fragment>
            {/* <img src={this.state.imageUrl}/> */}
            {/* two way to add style  */}
            {/* <span style={this.styles} className="badge badge-primary mr-2">{this.formateCounter()}</span> */}
            {/* <span style={{fontSize:100}} className="badge badge-primary mr-2">{this.formateCounter()}</span> */}

            <span className={this.getBadgeClasses()}>{this.formateCounter()}</span>
            <button className="btn btn-primary btn-sm">Increment</button>
        </React.Fragment>);
    }
    formateCounter = ()=>{
        const {count} = this.state;
        return count === 0? 'Zero': count;
    };

    getBadgeClasses() {
        let classes = "badge mr-2 badge-";
        classes += this.state.count === 0 ? "warning" : "primary";
        return classes;
    }
}

export default Counter;
